  const num = useNumber(10);
  const add2 = () => num.add(2);
  const subtract2 = () => num.subtract(2);
  const multiply2 = () => num.multiply(2);
  const divide2 = () => num.divide(2);
  const pow3 = () => num.pow(3);
  const sqrt = () => num.sqrt();
  return (
        <>
        <Button
            onClick={add2}
            size="large"
            type="primary"
            style={{ marginRight: '.5em' }}
        >
            Add 2
        </Button>
        <Button
            onClick={subtract2}
            size="large"
            type="primary"
        >
            Subtract 2
        </Button>
        <Input
            value={num.value}
            size="large"
            disabled
            style={{ marginTop: '1em' }}
        />
    </>
  )
}}
